if(int.TryParse(Console.ReadLine(), out int num1) && int.TryParse(Console.ReadLine(), out int num2)){
int sum = num1 + num2;
Console.WriteLine("ผลรวมคือ: " + sum);
} else {
Console.WriteLine("อินพุตไม่ถูกต้อง โปรดป้อนค่าตัวเลข");
} 
