//Configura la lista de datos a partir de la tabla de datos. Información y gráficos de la tabla
//Utilizado desde Code.org con permiso
var albumName = getColumn("ModifiedAlbums", "Album");
var albumYear = getColumn("ModifiedAlbums", "Year");
var genre = getColumn("ModifiedAlbums", "Genre");
var albumPic = getColumn("ModifiedAlbums", "Album Art");
//Configura listas filtradas
var filteredNameList = [];
var filteredPicList = [];
//Establece la pantalla de inicio como predeterminada
setScreen("StartScreen");
//llama a la función de filtro y a la función UpdateScreen cuando el usuario hace clic en PickAlbumButton
onEvent("PickAlbumButton", "click", function() {
filter();
updateScreen();
});
//La función de filtro borra filteredNameList y filteredPicList en las líneas 20 y 21.
//En las líneas 22 y 23, obtiene los datos que el usuario ingresó en los menús desplegables Año y Género y los guarda como variables.
//Las líneas 26 a 29 recorren la lista albumYear para encontrar los años que coinciden con el año ingresado por el usuario.
//Si el año coincide, se verifica el mismo elemento indexado en la lista de géneros.
//Si ambas condiciones son verdaderas, se selecciona el mismo elemento indexado de la lista albumName 
//y se agrega a la lista filteredName. El mismo elemento indexado también se selecciona de la lista albumPic
//y se agrega a filteredPicList
//Por lo tanto, filteredNameList y filteredPicList contienen solo elementos que coinciden con los criterios
//introducidos por el usuario. La función updateScreen utiliza estas listas de actualizaciones para completar
// resultsScreen.
function filter() {
filteredNameList = [];
filteredPicList = [];
var selectedYear = getText("year");
var selectedGenre = getText("Genre");

for (var i = 0; i < albumYear.length; i++) {
if (albumYear[i] == selectedYear && genre[i] == selectedGenre) {
appendItem(filteredNameList, albumName[i]);
appendItem(filteredPicList, albumPic[i]);
}
}
//Toma un elemento aleatorio de filteredPicList y lo muestra en ResultsScreen.
//También muestra una lista completa de todos los elementos de filteredNameList
//Si no hay elementos en filteredPicList, pasa a NoResultsScreen.
}
function updateScreen() {
if (filteredPicList.length>0) {
setProperty("AlbumPic", "image", filteredPicList[randomNumber(0, filteredPicList.length-1)]);
setText("RecommendedAlbums", filteredNameList.join(", "));
setScreen("ResultsScreen");
} else {
setScreen("NoResultsScreen");
}
}
