from rest\_framework import permissions, viewsets, generics, status
from rest\_framework.response import Response
from rest\_framework.exceptions import ValidationError
from .models import CertificationRequest, Status, Competence
from .serializers import CertificationRequestSerializer
from .permissions import IsVolunteer
# Crea tus vistas aquí.
class CertificationRequestViewSet(viewsets.ModelViewSet):
"""Conjunto de vistas para solicitudes de certificación"""
serializer\_class = CertificationRequestSerializer # usa la clase serializadora definida en serializers.py
permission\_classes = [permissions.IsAdminUser | IsVolunteer]

http\_method\_names = ['get', 'head', 'delete', 'post']

def get\_queryset(self):
"""Mostrar solo las solicitudes de certificación del usuario actual si no es administrador"""
if self.request.user.is\_staff:
return CertificationRequest.objects.all()
return CertificationRequest.objects.filter(user=self.request.user)

def perform\_create(self, serializer):
"""Crear una solicitud de certificación para el usuario actual si no es administrador"""
try:
if self.request.user.is\_staff:
raise ValidationError(
"Los administradores no pueden crear solicitudes de certificación")
# guardar la solicitud de certificación con el usuario actual
serializer.save(user=self.request.user)
except IntegrityError as e:
raise ValidationError(
'Ya existe una solicitud de certificación idéntica existe')
class AnswerCertificationRequest(generics.GenericAPIView):
"""Vista para responder solicitudes de certificación"""
permission\_classes = [permissions.IsAuthenticated]

def post(self, request):

# comprobar si se proporciona id y estado
if not(request.data.get('id') and request.data.get('status')):
return Response({'error': 'id y estado son obligatorios'}, status=status.HTTP\_400\_BAD\_REQUEST)

certification\_request = CertificationRequest.objects.get(
id=request.data['id'])

if certification\_request es None: # comprobar si existe una solicitud de certificación
return Response({'error': 'La solicitud de certificación no existe'}, status=status.HTTP\_400\_BAD\_REQUEST)

if certification\_request.status != 'P': # comprobar si la solicitud de certificación está pendiente
return Response({'error': 'La solicitud de certificación no está pendiente'}, status=status.HTTP\_400\_BAD\_REQUEST)

state = request.data['status']

if state == 'A': # si se acepta
certification\_request.status = Status.ACCEPTED
elif state == 'D': # si se rechaza
certification\_request.status = Status.DECLINED
else:
return Response({'error': 'El estado debe ser A o D'}, status=status.HTTP\_400\_BAD\_REQUEST)
certification\_request.save()
return Response(status=status.HTTP\_200\_OK)
class GetCertificationStatus(generics.GenericAPIView):
"""Vista para obtener la certificación estado.
Devuelve un diccionario con el estado de todas las competencias."""
permission\_classes = [permissions.IsAuthenticated]

def get(self, request):
result = {}
for s in Competence:

certification\_request = CertificationRequest.objects.filter(
user=self.request.user, competency=s).first()
if certification\_request:
body = {
'status': certification\_request.status,
'created': certification\_request.created,
'id': certification\_request.id
}
else:

body = {
'status': None,
'created': None,
'id': None
}
result[s] = body

return Response(result, status=status.HTTP\_200\_OK)

