namespace ConsoleAppTemplate
{
classe publique Fraction
{
privé en lecture seule int numérateur ;
privé en lecture seule int dénominateur ;

public Fraction(int numérateur = 0, int dénominateur = 1)
{
si (dénominateur == 0)
throw new ArgumentOutOfRangeException("den ne doit pas être 0 ");

// Assurez-vous que le dénominateur est positif
si (dénominateur < 0)
{
numérateur = -numérateur ;
dénominateur = -dénominateur ;
}

int gcd = FindGCD(numérateur, dénominateur );
this.numerator = numérateur / gcd ;
this.denominator = dénominateur / gcd ;
}

public override bool Equals(Object that)
{
return this == (Fraction)that;
}

public static bool operator==(Fraction a, Fraction b)
{
return a.numerator \* b.denominator == b.numerator \* a.denominator;
}

public static bool operator !=(Fraction a, Fraction b)
{
return !(a == b);
}
public static Fraction operator +(Fraction a, Fraction b)
{
int newNumerator = a.numerator \* b.denominator + b.numerator \* a.denominator;
int newDenominator = a.denominator \* b.denominator;
return new Fraction(newNumerator, newDenominator);

}

Opérateur de fraction statique public -(Fraction a, Fraction b)
{
int newNumerator = a.numerator \* b.denominator - b.numerator \* a.denominator;
int newDenominator = a.denominator \* b.denominator;
return new Fraction(newNumerator, newDenominator);
}

Opérateur de fraction statique public \*(Fraction a, Fraction b)
{
int newNumerator = a.numerator \* b.numerator;
int newDenominator = a.denominator \* b.denominator;
return new Fraction(newNumerator, newDenominator);
}

Opérateur de fraction statique public /(Fraction a, Fraction b)
{
int newNumerator = a.numerator \* b.denominator;
int newDenominator = a.denominator \* b.numerator;
return new Fraction(newNumerator, newDenominator); ;
}

public static Fraction operator -(Fraction a)
{
return new Fraction(-a.numerator, a.denominator);
}

public static implicit operator Fraction(int x)
{
return new Fraction(x);
}

public static explicit operator Fraction(double x)
{
const int accuracy = 1\_000\_000;
return new Fraction(
(int)(x \* accuracy), accuracy);
}

public static explicit operator int(Fraction a)
{
if (a.denominator != 1)
{
throw new InvalidCastException();
}

return a.numerator;
}

public static explicit operator double(Fraction a)
{
return a.numerator / a.denominator;
}

opérateur booléen statique public >(Fraction a, Fraction b)
{
int cd = a.denominator \* b.denominator;
int num1 = a.numerator \* cd;
int num2 = b.numerator \* cd;
return num1 > num2;
}

opérateur booléen statique public >=(Fraction a, Fraction b)
{
int cd = a.denominator \* b.denominator;
int num1 = a.numerator \* cd;
int num2 = b.numerator \* cd;
return num1 >= num2;

}

opérateur booléen statique public <=(Fraction a, Fraction b)
{
int cd = a.denominator \* b.denominator;
int num1 = a.numerator \* cd;
