kwa(int i=0;i<n;i++){
	cout<<num[i]<<endl;
}
    kurudi 0;
}
int Fz(int x,int m)
{
    int s=1,i;
    int y=(int)sqrt((double)x);
    kwa(i=m;i<=y;i++)
    {
        ikiwa(x%i==0)
        {
        s=s+Fz(x/i,i);
        }
    }  
    kurudi s;
}

