    haikuXML := "<haiku><line1><silabi>5</silabi><words>moyo unadunda kwa kasi sasa</words></line1><line2><silabi>7</silabi><words>kushikana mikono, kujisikia hai</maneno></line2><line3><silabi>5</silabi><words>mapenzi yanachanua ndani yangu nafsi</maneno></line3></haiku>"
    var haiku Haiku
    xml.Unmarshal([]byte(haikuXML), &haiku)
    line1Words = strings.Split(haiku.Line1.Words, " ")
    line2Words = strings.Split(haiku.Line2.Words, " ")
    line3Words = strings.Split(haiku.Line3.Words, " ")
