命名空间 ConsoleAppTemplate
{
public class Fraction
{
private readonly int numerator;
private readonly int denominator;

public Fraction(int numerator = 0, int denominator = 1)
{
if (denominator == 0)
throw new ArgumentOutOfRangeException("den must not be 0");

// 确保分母为正数
if (denominator < 0)
{
numerator = -numerator;
denominator = -denominator;
}

int gcd = FindGCD(numerator, denominator);
this.numerator = numerator / gcd;
this.denominator = denominator / gcd;
}

public override bool Equals(Object that)
{
return this == (Fraction)that;
}

public static bool operator==(Fraction a, Fraction b)
{
return a.numerator \* b.denominator == b.numerator \* a.denominator;
}

public static bool operator !=(Fraction a, Fraction b)
{
return !(a == b);
}
public static Fraction operator +(Fraction a, Fraction b)
{
int newNumerator = a.numerator \* b.denominator + b.numerator \* a.denominator;
int newDenominator = a.denominator \* b.denominator;
return new Fraction(newNumerator, newDenominator);

}

公共静态分数运算符 -(分数 a, 分数 b)
{
int newNumerator = a.numerator \* b.denominator - b.numerator \* a.denominator;
int newDenominator = a.denominator \* b.denominator;
返回新分数(newNumerator, newDenominator);
}

公共静态分数运算符 \*(分数 a, 分数 b)
{
int newNumerator = a.numerator \* b.numerator;
int newDenominator = a.denominator \* b.denominator;
返回新分数(newNumerator, newDenominator);
}

公共静态分数运算符 /(分数 a, 分数 b)
{
int newNumerator = a.numerator \* b.denominator;
int newDenominator = a.denominator \* b.numerator;
返回新的 Fraction(newNumerator, newDenominator); ;
}

公共静态 Fraction 运算符 -(Fraction a)
{
返回新的 Fraction(-a.numerator, a.denominator);
}

公共静态隐式运算符 Fraction(int x)
{
返回新的 Fraction(x);
}

公共静态显式运算符 Fraction(double x)
{
const int accuracy = 1\_000\_000;
返回新的 Fraction(
(int)(x \* accuracy), accuracy);
}

公共静态显式运算符 int(Fraction a)
{
if (a.denominator != 1)
{
抛出新的 InvalidCastException();
}

返回 a.numerator;
}

公共静态显式运算符 double(Fraction a)
{
返回 a.numerator / a.denominator;
}

public static bool 运算符 >(分数 a, 分数 b)
{
int cd = a.denominator \* b.denominator;
int num1 = a.numerator \* cd;
int num2 = b.numerator \* cd;
return num1 > num2;
}

public static bool 运算符 >=(分数 a, 分数 b)
{
int cd = a.denominator \* b.denominator;
int num1 = a.numerator \* cd;
int num2 = b.numerator \* cd;
return num1 >= num2;

}

public static bool 运算符 <=(分数 a, 分数 b)
{
int cd = a.denominator \* b.denominator;
int num1 = a.numerator \* cd;
int num2 = b.numerator \* cd;
返回 num1 <= num2;

}

public static bool operator <(Fraction a, Fraction b)
{
int cd = a.denominator \* b.denominator;
int num1 = a.numerator \* cd;
int num2 = b.numerator \* cd;
返回 num1 < num2;

}
private int FindGCD(int a, int b)
{
while (b != 0)
{
int remainder = a % b;
a = b;
b = remainder;
}
return a;
}

