certification\_request = CertificationRequest.objects.get(
id=request.data['id'])

if certification\_request is None: # 检查认证请求是否存在
return Response({'error': '认证请求不存在'}, status=status.HTTP\_400\_BAD\_REQUEST)

if certification\_request.status != 'P': # 检查认证请求是否待处理
return Response({'error': '认证请求未待处理'}, status=status.HTTP\_400\_BAD\_REQUEST)

state = request.data['status']

if state == 'A': # 如果已接受
certification\_request.status = Status.ACCEPTED
elif state == 'D': # 如果已拒绝
certification\_request.status = Status.DECLINED
else:
return Response({'error': '状态必须为 A 或 D'}, status=status.HTTP\_400\_BAD\_REQUEST)
